import React, { useEffect, useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom'
import { useAuth } from '../contexts/AuthContext'
import '../Styles/TariffsPage.css'

const TariffDetail = () => {
  const { id } = useParams()
  const [tariff, setTariff] = useState(null)
  const [loading, setLoading] = useState(true)
  const { user } = useAuth()
  const navigate = useNavigate()

  useEffect(() => {
    fetch(`/api/tariffs/${id}/`)
      .then(response => {
        if (!response.ok) {
          throw new Error('Ошибка при загрузке тарифа')
        }
        return response.json()
      })
      .then(data => {
        setTariff(data)
        setLoading(false)
      })
      .catch(error => {
        console.error(error)
        setLoading(false)
      })
  }, [id])

  const handleOrder = () => {
    if (!user) {
      navigate('/login')
      return
    }

    const orderData = {
      user: user.id,
      tariff: tariff.id,
      status: 'pending',
      custom_name: '',
    }

    fetch('/api/orders/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(orderData),
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Ошибка при создании заказа')
        }
        return response.json()
      })
      .then(data => {
        alert('Тариф успешно присвоен и заказ создан')
        navigate('/tariffs')
      })
      .catch(error => {
        console.error(error)
        alert('Ошибка при создании заказа')
      })
  }

  if (loading) {
    return <div className='loading-message'>Загрузка тарифа...</div>
  }

  if (!tariff) {
    return <div className='no-tariffs-message'>Тариф не найден.</div>
  }

  return (
    <div className='tariff-detail-container'>
      <h2 className='tariff-name'>{tariff.name}</h2>
      {tariff.photo && (
        <img
          src={`/images/${tariff.photo}`}
          alt={tariff.name}
          className='tariff-image'
        />
      )}
      <p className='tariff-description'>
        <strong>Краткое описание:</strong> {tariff.short_description}
      </p>
      <p className='tariff-description detailed'>
        <strong>Подробное описание:</strong> {tariff.detailed_description}
      </p>
      <p className='tariff-price'>
        <strong>Цена:</strong> {tariff.price} ₽
      </p>
      <pre style={{backgroundColor: '#eee', padding: '10px', borderRadius: '5px'}}>
        {JSON.stringify(tariff, null, 2)}
      </pre>
      <div className='tariff-buttons'>
        <button className='back-button' onClick={() => navigate('/tariffs')}>
          к тарифам
        </button>
        <button className='order-button' onClick={handleOrder}>
          оформить
        </button>
      </div>
    </div>
  )
}

export default TariffDetail
