import React, { useEffect, useState } from 'react'
import { useLocation } from 'react-router-dom'
import '../Styles/TariffsPage.css'

const Tariffs = () => {
	const [tariffs, setTariffs] = useState([])
	const [loading, setLoading] = useState(true)
	const location = useLocation()
	const clientType = location.state?.clientType || 'individual'

	useEffect(() => {
		fetch(`/api/tariffs/?client_type=${clientType}`)
			.then(response => response.json())
			.then(data => {
				setTariffs(data)
				setLoading(false)
			})
			.catch(error => {
				console.error('Ошибка при загрузке тарифов:', error)
				setLoading(false)
			})
	}, [clientType])

	if (loading) {
		return <div className='loading-message'>Загрузка тарифов...</div>
	}

	return (
		<div className='tariffs-container'>
			<h2 className='tariffs-title'>
				Тарифы для{' '}
				{clientType === 'individual' ? 'физических лиц' : 'юридических лиц'}
			</h2>
			{tariffs.length === 0 ? (
				<p className='no-tariffs-message'>Тарифы не найдены.</p>
			) : (
				<div className='tariffs-grid'>
					{tariffs.map(tariff => (
						<div key={tariff.id} className='tariff-card'>
							<div className='tariff-header'>
								<h3 className='tariff-name'>{tariff.name}</h3>
								{tariff.photo && (
									<img
										src={`/images/${tariff.photo}`}
										alt={tariff.name}
										className='tariff-image'
									/>
								)}
							</div>
							<div className='tariff-body'>
								<p className='tariff-description'>
									<strong>Краткое описание:</strong> {tariff.short_description}
								</p>
								<p className='tariff-description detailed'>
									<strong>Подробное описание:</strong>{' '}
									{tariff.detailed_description}
								</p>
								<p className='tariff-price'>
									<strong>Цена:</strong> {tariff.price} ₽
								</p>
							</div>
						</div>
					))}
				</div>
			)}
		</div>
	)
}

export default Tariffs
