import React, { useState, useEffect } from 'react'
import Chat from './Chat'

const ChatList = ({ accessToken }) => {
  const [chats, setChats] = useState([])
  const [selectedChatId, setSelectedChatId] = useState(null)

  useEffect(() => {
    const fetchChats = async () => {
      try {
        const response = await fetch('http://localhost:8000/chats/', {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        })
        if (!response.ok) {
          throw new Error('Ошибка загрузки чатов')
        }
        const data = await response.json()
        setChats(data)
      } catch (error) {
        console.error('Error fetching chats:', error)
      }
    }
    if (accessToken) {
      fetchChats()
    }
  }, [accessToken])

  return (
    <div className="chat-list-container" style={{ display: 'flex' }}>
      <div className="chat-list" style={{ width: '300px', borderRight: '1px solid #ccc', paddingRight: '10px' }}>
        <h3>Список чатов</h3>
        {chats.length === 0 ? (
          <p>Чатов нет</p>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {chats.map(chat => (
              <li key={chat.id} style={{ marginBottom: '10px', cursor: 'pointer', padding: '5px', backgroundColor: selectedChatId === chat.id ? '#e0e0e0' : 'transparent' }}
                onClick={() => setSelectedChatId(chat.id)}>
                <div><strong>Заказ:</strong> {chat.order_name}</div>
                <div><strong>Работник:</strong> {chat.worker_name}</div>
                <div><strong>Непрочитано:</strong> {chat.unread_messages}</div>
                <button onClick={(e) => { e.stopPropagation(); setSelectedChatId(chat.id) }}>Перейти к чату</button>
              </li>
            ))}
          </ul>
        )}
      </div>
      <div className="chat-window" style={{ flexGrow: 1, paddingLeft: '10px' }}>
        {selectedChatId ? (
          <Chat chatId={selectedChatId} accessToken={accessToken} />
        ) : (
          <p>Выберите чат</p>
        )}
      </div>
    </div>
  )
}

export default ChatList
