import React, { useState, useEffect, useRef } from 'react'
import '../Styles/Chat.css'

const Chat = ({ chatId, accessToken }) => {
  const [messages, setMessages] = useState([])
  const [newMessage, setNewMessage] = useState('')
  const messagesEndRef = useRef(null)

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await fetch(`http://localhost:8000/messages/?chat_id=${chatId}`, {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        })
        if (!response.ok) {
          throw new Error('Ошибка загрузки сообщений')
        }
        const data = await response.json()
        setMessages(data)
      } catch (error) {
        console.error('Error fetching messages:', error)
      }
    }
    if (chatId && accessToken) {
      fetchMessages()
    }
  }, [chatId, accessToken])

  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' })
    }
  }, [messages])

  const handleSendMessage = async () => {
    if (!newMessage.trim()) return
    try {
      const response = await fetch('http://localhost:8000/messages/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${accessToken}`,
        },
        body: JSON.stringify({
          chat: chatId,
          text: newMessage.trim(),
        }),
      })
      if (!response.ok) {
        throw new Error('Ошибка отправки сообщения')
      }
      const savedMessage = await response.json()
      setMessages(prev => [...prev, savedMessage])
      setNewMessage('')
    } catch (error) {
      console.error('Error sending message:', error)
    }
  }

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault()
      handleSendMessage()
    }
  }

  // Определяем текущего пользователя для позиционирования сообщений
  // Предполагается, что backend возвращает sender_user или sender_worker с id
  // Для упрощения считаем, что свои сообщения имеют sender_user или sender_worker не null и совпадают с текущим пользователем
  // Для этого нужно получить id текущего пользователя и работника из контекста или пропсов
  // Здесь для упрощения позиционируем по sender_user != null (пользователь) и sender_worker != null (работник)
  // В реальном проекте нужно передавать id текущего пользователя и работника

  return (
    <div className="chat-container">
      <div className="messages">
        {messages.map(message => {
          const isUserMessage = message.sender_user !== null
          return (
            <div key={message.id} className={`message ${isUserMessage ? 'user' : 'other'}`}>
              <div className="message-content">
                {message.text}
              </div>
            </div>
          )
        })}
        <div ref={messagesEndRef} />
      </div>
      <textarea
        className="message-input"
        value={newMessage}
        onChange={e => setNewMessage(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder="Введите сообщение..."
      />
      <button onClick={handleSendMessage} className="send-button">Отправить</button>
    </div>
  )
}

export default Chat
